        <>
            <div className="fixed-top container">
                <div className="row">
                    <div id="nav" className="col-sm-4 p-5 shadow">
                        <div className="py-1">
                            <h1>Weather</h1>
                            <p className="font-italic font-weight-light">Providing your local weather information with mad style.</p>
                            <button
                                onClick={() => search.set(!search.state)}
                                className="btn text-secondary"
                            >
                                <i className="fa fa-search"></i>
                            </button>
                            <input
                                className={`h-100 p-2 bg-light ${!search.state ? 'invisible' : ''}`}
                                type="text"
                                name="name"
                                placeholder="search"
                            />
                            <div className="line"></div>
                        </div>
                        <div className="py-4">
                            <h2 className="font-weight-bold">Locations</h2>

                        </div>
                    </div>
                </div>
            </div>


        </>
    )
}
export default Navigation
